import React, { useEffect, useState } from "react";
import axios from "axios";
import "../styles/Wallet.css";

const API_URL = "http://localhost:1212/api";

const Wallet = ({ token, userId }) => {
  const [wallet, setWallet] = useState(null);
  const [amount, setAmount] = useState(1000);
  const [message, setMessage] = useState("");
  const [loading, setLoading] = useState(true);

  const fetchWallet = async () => {
    try {
      const res = await axios.get(`${API_URL}/wallets/${userId}`, {
        headers: { Authorization: `Bearer ${token}` },
      });

      setWallet(res.data);
      setMessage("‚úÖ Wallet loaded.");
    } catch (err) {
      if (err.response && err.response.status === 404) {
        // Wallet doesn't exist ‚Äî create one
        console.warn("Wallet not found, creating...");
        await createWallet();
      } else {
        console.error("Error fetching wallet:", err);
        setMessage("‚ùå Failed to fetch wallet.");
      }
    } finally {
      setLoading(false);
    }
  };

  const createWallet = async () => {
    try {
      const res = await axios.post(`${API_URL}/wallets/create?userId=${userId}`, null, {
        headers: { Authorization: `Bearer ${token}` },
      });

      console.log("Wallet created:", res.data);
      setMessage("‚úÖ Wallet created.");
      // Fetch again to get details
      const walletRes = await axios.get(`${API_URL}/wallets/${userId}`, {
        headers: { Authorization: `Bearer ${token}` },
      });
      setWallet(walletRes.data);
    } catch (err) {
      console.error("Wallet creation failed:", err);
      setMessage("‚ùå Wallet creation failed.");
    }
  };

  const topUpWallet = async () => {
    try {
      const res = await axios.post(
        `${API_URL}/wallets/add`,
        { userId, amount },
        {
          headers: { Authorization: `Bearer ${token}` },
        }
      );

      setMessage("‚úÖ Wallet topped up!");
      fetchWallet(); // Refresh balance
    } catch (err) {
      console.error("Top-up failed:", err);
      setMessage("‚ùå Top-up failed.");
    }
  };

  useEffect(() => {
    if (token && userId) {
      fetchWallet();
    }
  }, [token, userId]);

  return (
    <div className="wallet-container">
      <h2>üí∞ Your Wallet</h2>

      {loading ? (
        <p>Loading wallet...</p>
      ) : wallet ? (
        <>
          <p>Current Balance: ‚Çπ{wallet.balance.toLocaleString()}</p>
          <div className="top-up-section">
            <input
              type="number"
              value={amount}
              onChange={(e) => setAmount(Number(e.target.value))}
              min={100}
              step={100}
            />
            <button onClick={topUpWallet}>Top Up</button>
          </div>
        </>
      ) : (
        <p>‚ùå Wallet not available.</p>
      )}

      {message && <p className="wallet-message">{message}</p>}
    </div>
  );
};

export default Wallet;
