// src/App.js
import React, { useEffect, useState } from "react";
import { BrowserRouter as Router, Routes, Route, Navigate } from "react-router-dom";
import HomePage from "./pages/HomePage";
import LoginPage from "./pages/LoginPage";
import RegisterPage from "./pages/RegisterPage";
import WalletPage from "./pages/WalletPage";
import BookingHistoryPage from "./pages/BookingHistoryPage";
import FlightPage from "./pages/FlightPage";
import Dashboard from "./pages/Dashboard";
import AdminDashboard from "./pages/AdminDashboard";
import Navbar from "./components/Navbar";

function App() {
  const [token, setToken] = useState(null);
  const [user, setUser] = useState(null);

  useEffect(() => {
    const storedToken = localStorage.getItem("token");
    const storedUser = JSON.parse(localStorage.getItem("user"));

    if (storedToken && storedUser?.userRole) {
      setToken(storedToken);
      setUser(storedUser);
    }
  }, []);

  const handleLogout = () => {
    localStorage.clear();
    setToken(null);
    setUser(null);
  };

  return (
    <Router>
      <Navbar user={user} onLogout={handleLogout} />
      <Routes>
        <Route path="/" element={<HomePage />} />
        <Route path="/login" element={<LoginPage />} />
        <Route path="/register" element={<RegisterPage />} />
        <Route path="/wallet" element={<WalletPage />} />
        <Route
          path="/flights"
          element={
            token ? <FlightPage token={token} userId={user?.userId} /> : <Navigate to="/login" />
          }
        />
        <Route
          path="/bookings"
          element={
            token ? <BookingHistoryPage token={token} userId={user?.userId} /> : <Navigate to="/login" />
          }
        />

        {/* Customer Dashboard */}
        <Route
          path="/dashboard"
          element={
            token && user?.userRole === "CUSTOMER" ? (
              <Dashboard token={token} userId={user.userId} onLogout={handleLogout} />
            ) : (
              <Navigate to="/login" />
            )
          }
        />

        {/* Admin Dashboard */}
        <Route
          path="/admin"
          element={
            token && user?.userRole === "ADMIN" ? (
              <AdminDashboard token={token} user={user} onLogout={handleLogout} />
            ) : (
              <Navigate to="/login" />
            )
          }
        />
      </Routes>
    </Router>
  );
}

export default App;
