// src/pages/FlightPage.js
import React, { useEffect, useState } from "react";
import axios from "axios";
import "../styles/FlightPage.css";

const API_URL = "http://localhost:1212/api";

const FlightPage = ({ token, userId }) => {
  const [flights, setFlights] = useState([]);
  const [loading, setLoading] = useState(false);

  const fetchFlights = async () => {
    try {
      setLoading(true);
      const res = await axios.get(`${API_URL}/flights`, {
        headers: { Authorization: `Bearer ${token}` },
      });
      setFlights(res.data);
    } catch (err) {
      console.error("Failed to fetch flights", err);
    } finally {
      setLoading(false);
    }
  };

  const bookFlight = async (flightId) => {
    try {
      await axios.post(
        `${API_URL}/bookings`,
        { userId, flightId },
        { headers: { Authorization: `Bearer ${token}` } }
      );
      alert("Flight booked successfully!");
    } catch (err) {
      alert("Booking failed");
    }
  };

  useEffect(() => {
    if (token) {
      fetchFlights();
    }
  }, [token]);

  return (
    <div className="flight-wrapper">
      <div className="flight-container">
        <h2>Available Flights</h2>
        {loading ? (
          <p>Loading flights...</p>
        ) : flights.length > 0 ? (
          <ul className="flight-list">
            {flights.map((flight) => (
              <li key={flight.flightId} className="flight-item">
                <p>
                  ✈️ {flight.flightNumber} - {flight.source} to{" "}
                  {flight.destination} on {flight.departureDate}
                </p>
                <button onClick={() => bookFlight(flight.flightId)}>
                  Book Flight
                </button>
              </li>
            ))}
          </ul>
        ) : (
          <p>No flights available</p>
        )}
      </div>
    </div>
  );
};

export default FlightPage;
