import React, { useState, useEffect } from "react";
import { BrowserRouter as Router, Routes, Route, Navigate } from "react-router-dom";
import LoginPage from "./pages/LoginPage";
import Dashboard from "./pages/Dashboard";
import AdminDashboard from "./pages/AdminDashboard";

function App() {
  const [token, setToken] = useState(localStorage.getItem("token") || "");
  const [user, setUser] = useState(() => {
    const storedUser = localStorage.getItem("user");
    return storedUser ? JSON.parse(storedUser) : null;
  });

  const handleLogin = (userData, token) => {
    setUser(userData);
    setToken(token);
  };

  const handleLogout = () => {
    setUser(null);
    setToken("");
    localStorage.clear();
  };

  return (
    <Router>
      <Routes>
        <Route
          path="/"
          element={user ? (user.userRole === "ADMIN" ? <Navigate to="/admin" /> : <Navigate to="/dashboard" />) : <Navigate to="/login" />}
        />
        <Route path="/login" element={<LoginPage onLogin={handleLogin} />} />
        <Route
          path="/dashboard"
          element={
            user?.userRole === "CUSTOMER" ? (
              <Dashboard token={token} userId={user.userId} onLogout={handleLogout} />
            ) : (
              <Navigate to="/login" />
            )
          }
        />
        <Route
          path="/admin"
          element={
            user?.userRole === "ADMIN" ? (
              <AdminDashboard token={token} userRole={user.userRole} />
            ) : (
              <Navigate to="/login" />
            )
          }
        />
      </Routes>
    </Router>
  );
}

export default App;
