import React, { useState, useEffect, useRef } from "react";
import { Link, useNavigate } from "react-router-dom";
import "animate.css";
import "../styles/Navbar.css";

const Navbar = ({ user, onLogout }) => {
  const navigate = useNavigate();
  const [clicks, setClicks] = useState(0);
  const [showDropdown, setShowDropdown] = useState(false);
  const timerRef = useRef(null);
  const clickResetRef = useRef(null);

  const handleLogout = () => {
    onLogout();
    navigate("/");
  };

  const handleLogoClick = () => {
    setClicks((prev) => prev + 1);
    clearTimeout(clickResetRef.current);

    clickResetRef.current = setTimeout(() => setClicks(0), 600);
  };

  useEffect(() => {
    if (clicks === 3) {
      setShowDropdown(true);
      setClicks(0); // reset the click count

      // Auto-hide dropdown after 3s
      clearTimeout(timerRef.current);
      timerRef.current = setTimeout(() => setShowDropdown(false), 3000);
    }

    return () => {
      clearTimeout(timerRef.current);
      clearTimeout(clickResetRef.current);
    };
  }, [clicks]);

  return (
    <nav className="navbar animate__animated animate__fadeInDown">
      <div className="logo" onClick={handleLogoClick}>
        <Link to="/">üõ´ FlyFast</Link>
        {showDropdown && (
          <div className="dropdown animate__animated animate__fadeInDown">
            <span className="glow-text">‚ú® Sheik Irfan ‚ú®</span>
          </div>
        )}
      </div>

      <ul className="nav-links">
        {!user ? (
          <>
            <li><Link to="/login">üîê Login</Link></li>
            <li><Link to="/register">üìù Register</Link></li>
          </>
        ) : (
          <>
            <li className="user-info">üë§ {user.userEmail}</li>
            <li><button onClick={handleLogout} className="logout-btn">üö™ Logout</button></li>
          </>
        )}
      </ul>
    </nav>
  );
};

export default Navbar;
