// src/pages/AdminDashboard.js
import React, { useEffect, useState } from "react";
import axios from "axios";
import "../styles/AdminDashboard.css";

const API_URL = "http://localhost:1212/api";

const AdminDashboard = ({ token, onLogout }) => {
  const [flights, setFlights] = useState([]);
  const [airports, setAirports] = useState([]);
  const [airplanes, setAirplanes] = useState([]);
  const [users, setUsers] = useState([]);

  useEffect(() => {
    fetchAll();
  }, []);

  const fetchAll = async () => {
    try {
      const config = { headers: { Authorization: `Bearer ${token}` } };
      const [flightRes, airportRes, airplaneRes, userRes] = await Promise.all([
        axios.get(`${API_URL}/flights`, config),
        axios.get(`${API_URL}/airports`, config),
        axios.get(`${API_URL}/airplanes`, config),
        axios.get(`${API_URL}/users`, config)
      ]);
      setFlights(flightRes.data);
      setAirports(airportRes.data);
      setAirplanes(airplaneRes.data);
      setUsers(userRes.data);
    } catch (err) {
      console.error("Error fetching admin data", err);
    }
  };

  const deleteItem = async (endpoint, id) => {
    try {
      await axios.delete(`${API_URL}/${endpoint}/${id}`, {
        headers: { Authorization: `Bearer ${token}` },
      });
      fetchAll();
    } catch (err) {
      alert("Delete failed");
    }
  };

  const renderList = (title, data, fields, endpoint) => (
    <div className="admin-section">
      <h3>{title}</h3>
      <ul>
        {data.map((item) => (
          <li key={item.id || item.flightId || item.airportId || item.userId}>
            {fields.map((field) => (
              <span key={field}>{item[field]} </span>
            ))}
            <button onClick={() => deleteItem(endpoint, item.id || item.flightId || item.airportId || item.userId)}>ðŸ—‘ Delete</button>
          </li>
        ))}
      </ul>
    </div>
  );

  return (
    <div className="admin-dashboard">
      <header>
        <h2>Admin Dashboard</h2>
        <button onClick={onLogout}>Logout</button>
      </header>
      <main>
        {renderList("Flights", flights, ["flightNumber", "source", "destination"], "flights")}
        {renderList("Airports", airports, ["airportName", "city"], "airports")}
        {renderList("Airplanes", airplanes, ["airplaneModel", "capacity"], "airplanes")}
        {renderList("Users", users, ["userName", "userEmail", "role"], "users")}
      </main>
    </div>
  );
};

export default AdminDashboard;
