import React, { useEffect, useState } from "react";
import axios from "axios";
import "../styles/AdminDashboard.css";

const API_URL = "http://localhost:1212/api";

const AdminDashboard = ({ token, onLogout }) => {
  const [flights, setFlights] = useState([]);
  const [newFlight, setNewFlight] = useState({
    flightNumber: "",
    source: "",
    destination: "",
    departureDate: "",
  });

  const fetchFlights = async () => {
    try {
      const res = await axios.get(`${API_URL}/flights`, {
        headers: { Authorization: `Bearer ${token}` },
      });
      setFlights(res.data);
    } catch (err) {
      console.error("Error fetching flights", err);
    }
  };

  const handleInputChange = (e) => {
    setNewFlight({ ...newFlight, [e.target.name]: e.target.value });
  };

  const addFlight = async () => {
    try {
      await axios.post(`${API_URL}/flights`, newFlight, {
        headers: { Authorization: `Bearer ${token}` },
      });
      setNewFlight({ flightNumber: "", source: "", destination: "", departureDate: "" });
      fetchFlights();
    } catch (err) {
      console.error("Error adding flight", err);
    }
  };

  const deleteFlight = async (flightId) => {
    try {
      await axios.delete(`${API_URL}/flights/${flightId}`, {
        headers: { Authorization: `Bearer ${token}` },
      });
      fetchFlights();
    } catch (err) {
      console.error("Error deleting flight", err);
    }
  };

  useEffect(() => {
    fetchFlights();
  }, []);

  return (
    <div className="admin-dashboard">
      <aside className="sidebar">
        <h2>Admin Dashboard</h2>
        <ul>
          <li>ðŸ›« Flights</li>
          {/* Add navigation for Airports, Airplanes, Users, etc later */}
          <li><button className="logout-btn" onClick={onLogout}>Logout</button></li>
        </ul>
      </aside>

      <main className="main-content">
        <h1>Manage Flights</h1>

        <div className="form">
          <input name="flightNumber" value={newFlight.flightNumber} onChange={handleInputChange} placeholder="Flight Number" />
          <input name="source" value={newFlight.source} onChange={handleInputChange} placeholder="Source" />
          <input name="destination" value={newFlight.destination} onChange={handleInputChange} placeholder="Destination" />
          <input name="departureDate" type="date" value={newFlight.departureDate} onChange={handleInputChange} />
          <button onClick={addFlight}>Add Flight</button>
        </div>

        <ul className="flight-list">
          {flights.map((f) => (
            <li key={f.flightId}>
              {f.flightNumber} - {f.source} to {f.destination} on {f.departureDate}
              <button onClick={() => deleteFlight(f.flightId)}>Delete</button>
            </li>
          ))}
        </ul>
      </main>
    </div>
  );
};

export default AdminDashboard;
