// src/pages/Dashboard.js
import React, { useState, useEffect } from "react";
import axios from "axios";
import { Link } from "react-router-dom";
import "../styles/Dashboard.css";
import "animate.css";

const API_URL = "http://localhost:1212/api";

const Dashboard = ({ token, userId, onLogout }) => {
  const [flights, setFlights] = useState([]);
  const [wallet, setWallet] = useState(null);
  const [airplanes, setAirplanes] = useState([]);

  const fetchFlights = async () => {
    try {
      const res = await axios.get(`${API_URL}/flights`, {
        headers: { Authorization: `Bearer ${token}` },
      });
      setFlights(res.data);
    } catch (err) {
      console.error("Failed to fetch flights", err);
    }
  };

  const fetchWallet = async () => {
    if (!userId) return;
    try {
      const res = await axios.get(`${API_URL}/wallets/${userId}`, {
        headers: { Authorization: `Bearer ${token}` },
      });
      setWallet(res.data);
    } catch (err) {
      console.error("Failed to fetch wallet", err);
    }
  };

  const fetchAirplanes = async () => {
    try {
      const res = await axios.get(`${API_URL}/airplanes`, {
        headers: { Authorization: `Bearer ${token}` },
      });
      setAirplanes(res.data);
    } catch (err) {
      console.error("Failed to fetch airplanes", err);
    }
  };

  const bookFlight = async (flightId) => {
    try {
      await axios.post(
        `${API_URL}/bookings`,
        { userId, flightId },
        { headers: { Authorization: `Bearer ${token}` } }
      );
      alert("üéâ Flight booked successfully!");
    } catch (err) {
      alert("‚ùå Booking failed");
    }
  };

  const topUpWallet = async (amount) => {
    try {
      await axios.post(
        `${API_URL}/wallets/topup`,
        { userId, amount },
        { headers: { Authorization: `Bearer ${token}` } }
      );
      fetchWallet();
      alert("‚úÖ Wallet topped up successfully!");
    } catch (err) {
      alert("‚ùå Top-up failed");
    }
  };

  useEffect(() => {
    fetchFlights();
    fetchWallet();
    fetchAirplanes();
  }, []);

  return (
    <div className="dashboard-container animate__animated animate__fadeIn">
      <aside className="sidebar animate__animated animate__fadeInLeft animate__delay-1s">
        <h2 className="animate__animated animate__fadeInDown animate__faster">Dashboard</h2>
        <nav>
          <ul>
            <li><Link to="/flights">‚úàÔ∏è View Flights</Link></li>
            <li><Link to="/airports">üõ´ Airports</Link></li>
            <li><Link to="/airplanes">üõ¨ Airplanes</Link></li>
            <li><Link to="/wallet">üí∞ Wallet</Link></li>
            <li><Link to="/bookings">üìÑ Booking History</Link></li>
            {userId && <li><Link to="/admin">üõ† Admin Dashboard</Link></li>}
            <li><button className="logout-btn" onClick={onLogout}>üö™ Logout</button></li>
          </ul>
        </nav>
      </aside>

      <main className="dashboard-main animate__animated animate__fadeInUp animate__delay-1s">
        <h1 className="animate__animated animate__zoomIn">üéØ Welcome to Your Flight Dashboard</h1>

        <h2>Available Flights</h2>
        <ul className="flight-list">
          {flights.map((flight) => (
            <li key={flight.flightId} className="animate__animated animate__fadeInUp animate__faster">
              ‚úàÔ∏è <strong>{flight.flightNumber}</strong> ‚Äî {flight.source} ‚Üí {flight.destination} on <em>{flight.departureDate}</em>
              <button onClick={() => bookFlight(flight.flightId)}>Book</button>
            </li>
          ))}
        </ul>

        <h2>Airplanes</h2>
        <ul className="flight-list">
          {airplanes.map((airplane) => (
            <li key={airplane.airplaneId} className="animate__animated animate__fadeInUp animate__faster">
              üõ¨ <strong>{airplane.model}</strong> ‚Äî {airplane.manufacturer} | Capacity: {airplane.capacity}
            </li>
          ))}
        </ul>

        <h2>Wallet</h2>
        {wallet ? (
          <p className="animate__animated animate__fadeIn">üí∞ Balance: ‚Çπ{wallet.balance.toLocaleString()}</p>
        ) : (
          <button onClick={fetchWallet}>Load Wallet</button>
        )}
        <div>
          <button onClick={() => topUpWallet(1000)}>Top Up ‚Çπ1000</button>
        </div>
      </main>
    </div>
  );
};

export default Dashboard;
