// src/pages/AdminDashboard.js
import React, { useEffect, useState } from "react";
import axios from "axios";
import { useNavigate } from "react-router-dom";
import "../styles/AdminDashboard.css";
import "animate.css";

const API_URL = "http://localhost:1212/api";

const AdminDashboard = ({ token, userRole }) => {
  const [stats, setStats] = useState({});
  const navigate = useNavigate();

  useEffect(() => {
    if (!token || userRole !== "ADMIN") {
      navigate("/login");
    }
  }, [token, userRole, navigate]);

  useEffect(() => {
    const fetchStats = async () => {
      try {
        const res = await axios.get(`${API_URL}/admin/stats`, {
          headers: { Authorization: `Bearer ${token}` },
        });
        setStats(res.data);
      } catch (err) {
        console.error("Failed to load admin stats", err);
      }
    };

    fetchStats();
  }, [token]);

  return (
    <div className="admin-dashboard animate__animated animate__fadeIn">
      <h1 className="animate__animated animate__bounceInDown">üõ† Admin Dashboard</h1>
      <div className="stats-grid animate__animated animate__fadeInUp animate__delay-1s">
        <div className="card">
          <h3>üë§ Total Users</h3>
          <p>{stats.totalUsers || 0}</p>
        </div>
        <div className="card">
          <h3>‚úàÔ∏è Total Flights</h3>
          <p>{stats.totalFlights || 0}</p>
        </div>
        <div className="card">
          <h3>üìÑ Total Bookings</h3>
          <p>{stats.totalBookings || 0}</p>
        </div>
        <div className="card">
          <h3>üí∏ Total Wallet Funds</h3>
          <p>‚Çπ{stats.totalWalletBalance?.toLocaleString() || 0}</p>
        </div>
      </div>
    </div>
  );
};

export default AdminDashboard;
