// src/pages/Airports.js
import React, { useEffect, useState } from "react";
import axios from "axios";
import "animate.css";
import "../styles/Airports.css";

const API_URL = "http://localhost:1212/api";

const Airports = ({ token }) => {
  const [airports, setAirports] = useState([]);
  const [error, setError] = useState("");

  const fetchAirports = async () => {
    try {
      const res = await axios.get(`${API_URL}/airports`, {
        headers: { Authorization: `Bearer ${token}` }
      });
      setAirports(res.data);
    } catch (err) {
      console.error("Error fetching airports:", err);
      setError("‚ùå Failed to load airports. Please try again.");
    }
  };

  useEffect(() => {
    fetchAirports();
  }, []);

  return (
    <div className="airports-container animate__animated animate__fadeIn">
      <h2 className="animate__animated animate__fadeInDown">üõ´ Airports</h2>
      {error && <p className="error-msg">{error}</p>}
      <ul className="airport-list">
        {airports.map((airport) => (
          <li key={airport.airportId} className="airport-card animate__animated animate__fadeInUp">
            <h3>{airport.airportName} ({airport.airportCode})</h3>
            <p>üìç {airport.airportCity}, {airport.airportState}, {airport.airportCountry}</p>
          </li>
        ))}
      </ul>
    </div>
  );
};

export default Airports;
