// src/pages/Dashboard.js
import React, { useState, useEffect } from "react";
import axios from "axios";
import Sidebar from "../components/Sidebar";
import "./Dashboard.css";

const API_URL = "http://localhost:1212/api";

const Dashboard = ({ token, userId, onLogout }) => {
  const [flights, setFlights] = useState([]);
  const [wallet, setWallet] = useState(null);

  const fetchFlights = async () => {
    try {
      const res = await axios.get(`${API_URL}/flights`, {
        headers: { Authorization: `Bearer ${token}` },
      });
      setFlights(res.data);
    } catch (err) {
      console.error("Failed to fetch flights", err);
    }
  };

  const fetchWallet = async () => {
    if (!userId) return;
    try {
      const res = await axios.get(`${API_URL}/wallets/${userId}`, {
        headers: { Authorization: `Bearer ${token}` },
      });
      setWallet(res.data);
    } catch (err) {
      console.error("Failed to fetch wallet", err);
    }
  };

  const bookFlight = async (flightId) => {
    try {
      await axios.post(
        `${API_URL}/bookings`,
        { userId, flightId },
        { headers: { Authorization: `Bearer ${token}` } }
      );
      alert("Flight booked successfully");
    } catch (err) {
      alert("Booking failed");
    }
  };

  const topUpWallet = async (amount) => {
    try {
      await axios.post(
        `${API_URL}/wallets/topup`,
        { userId, amount },
        { headers: { Authorization: `Bearer ${token}` } }
      );
      fetchWallet();
      alert("Wallet topped up");
    } catch (err) {
      alert("Top-up failed");
    }
  };

  useEffect(() => {
    fetchFlights();
  }, []);

  return (
    <div className="dashboard">
      <Sidebar onLogout={onLogout} />
      <div className="content">
        <h2>Flights</h2>
        <ul>
          {flights.map((flight) => (
            <li key={flight.flightId}>
              {flight.flightNumber} - {flight.source} to {flight.destination} on {flight.departureDate}
              <button onClick={() => bookFlight(flight.flightId)} style={{ marginLeft: 10 }}>Book</button>
            </li>
          ))}
        </ul>

        <h2>Wallet</h2>
        {wallet ? (
          <p>Balance: ${wallet.balance}</p>
        ) : (
          <button onClick={fetchWallet}>Load Wallet</button>
        )}
        <div>
          <button onClick={() => topUpWallet(100)}>Top Up $100</button>
        </div>
      </div>
    </div>
  );
};

export default Dashboard;
