import React, { useEffect, useState } from "react";
import axios from "axios";
import "../styles/Wallet.css";

const API_URL = "http://localhost:1212/api";

const Wallet = ({ token, userId, userRole }) => {
  const [wallet, setWallet] = useState(null);
  const [amount, setAmount] = useState(1000);
  const [message, setMessage] = useState("");

  const fetchWallet = async () => {
    if (userRole !== "CUSTOMER") {
      setMessage("âŒ Wallet is only available for customers.");
      return;
    }

    try {
      const res = await axios.get(`${API_URL}/wallets/${userId}`, {
        headers: { Authorization: `Bearer ${token}` },
      });
      setWallet(res.data);
    } catch (err) {
      if (err.response && err.response.status === 404) {
        // Wallet doesn't exist, try creating it
        try {
          const res = await axios.post(
            `${API_URL}/wallets/create?userId=${userId}`,
            {},
            {
              headers: { Authorization: `Bearer ${token}` },
            }
          );
          setWallet(res.data);
        } catch (createErr) {
          console.error("Error creating wallet:", createErr);
          setMessage("âŒ Failed to create wallet.");
        }
      } else {
        console.error("Error loading wallet:", err);
        setMessage("âŒ Could not fetch wallet.");
      }
    }
  };

  const topUpWallet = async () => {
    try {
      await axios.post(
        `${API_URL}/wallets/add`,
        { userId, amount },
        {
          headers: { Authorization: `Bearer ${token}` },
        }
      );
      setMessage("âœ… Wallet topped up!");
      fetchWallet();
    } catch (err) {
      console.error("Top-up failed:", err);
      setMessage("âŒ Top-up failed.");
    }
  };

  useEffect(() => {
    if (token && userId) {
      fetchWallet();
    }
  }, [token, userId]);

  return (
    <div className="wallet-container">
      <h2>ðŸ’° Your Wallet</h2>
      {userRole !== "CUSTOMER" ? (
        <p>Wallet is only available for customers.</p>
      ) : wallet ? (
        <>
          <p>Current Balance: â‚¹{wallet.balance.toLocaleString()}</p>
          <div className="top-up-section">
            <input
              type="number"
              value={amount}
              onChange={(e) => setAmount(Number(e.target.value))}
              min={100}
              step={100}
            />
            <button onClick={topUpWallet}>Top Up</button>
          </div>
        </>
      ) : (
        <p>Loading wallet...</p>
      )}
      {message && <p className="wallet-message">{message}</p>}
    </div>
  );
};

export default Wallet;
