// src/pages/Airports.js
import React, { useEffect, useState } from "react";
import axios from "axios";
import "../styles/Airports.css";
import "animate.css";

const API_URL = "http://localhost:1212/api";

const Airports = ({ token }) => {
  const [airports, setAirports] = useState([]);

  const fetchAirports = async () => {
    try {
      const res = await axios.get(`${API_URL}/airports`, {
        headers: { Authorization: `Bearer ${token}` },
      });
      setAirports(res.data);
    } catch (err) {
      console.error("Failed to fetch airports", err);
    }
  };

  useEffect(() => {
    fetchAirports();
  }, []);

  return (
    <div className="airports-container animate__animated animate__fadeIn">
      <h1 className="animate__animated animate__fadeInDown">ðŸ›« Airport Directory</h1>
      <ul className="airport-list">
        {airports.map((airport) => (
          <li key={airport.airportId} className="animate__animated animate__fadeInUp">
            <strong>{airport.name}</strong> â€” {airport.city}, {airport.country}
          </li>
        ))}
      </ul>
    </div>
  );
};

export default Airports;
