// src/pages/LoginPage.js
import React, { useState } from "react";
import axios from "axios";
import { useNavigate } from "react-router-dom";
import "animate.css";
import "../styles/Auth.css"; // üîß Make sure this file exists or create it

const API_URL = "http://localhost:1212/api";

const LoginPage = ({ onLogin }) => {
  const [userEmail, setUserEmail] = useState("");
  const [userPassword, setUserPassword] = useState("");
  const [rememberMe, setRememberMe] = useState(false);
  const [error, setError] = useState("");
  const navigate = useNavigate();

  const handleLogin = async (e) => {
    e.preventDefault();
    setError("");

    try {
      const res = await axios.post(`${API_URL}/login`, {
        userEmail,
        userPassword,
      });

      const { token, role, id } = res.data;
      const user = { userEmail, userRole: role, userId: id };

      if (rememberMe) {
        localStorage.setItem("token", token);
        localStorage.setItem("user", JSON.stringify(user));
      } else {
        sessionStorage.setItem("token", token);
        sessionStorage.setItem("user", JSON.stringify(user));
      }

      onLogin(token, user);

      if (role === "CUSTOMER") {
        navigate("/dashboard");
      } else if (role === "ADMIN") {
        navigate("/admin-dashboard");
      } else {
        setError("Access denied: Not a recognized role.");
      }
    } catch (err) {
      console.error(err);
      setError("‚ùå Invalid email or password.");
    }
  };

  return (
    <div className="auth-container animate__animated animate__fadeIn">
      <h2 className="animate__animated animate__fadeInDown">üîê Login</h2>
      <form
        onSubmit={handleLogin}
        className="auth-form animate__animated animate__fadeInUp animate__delay-1s"
      >
        <input
          type="email"
          placeholder="‚úâÔ∏è Email"
          value={userEmail}
          onChange={(e) => setUserEmail(e.target.value)}
          required
        />
        <input
          type="password"
          placeholder="üîë Password"
          value={userPassword}
          onChange={(e) => setUserPassword(e.target.value)}
          required
        />

        <label className="remember-label">
          <input
            type="checkbox"
            checked={rememberMe}
            onChange={(e) => setRememberMe(e.target.checked)}
          />
          Remember me
        </label>

        <button type="submit" className="login-btn">üöÄ Login</button>

        {error && <p className="error animate__animated animate__shakeX">{error}</p>}
      </form>
    </div>
  );
};

export default LoginPage;
