import React, { useEffect, useState } from "react";
import axios from "axios";
import "../styles/AdminFlights.css";

const API_URL = "http://localhost:1212/api";

const AdminFlights = ({ token }) => {
  const [flights, setFlights] = useState([]);
  const [searchTerm, setSearchTerm] = useState("");
  const [departureDate, setDepartureDate] = useState("");
  const [airplaneId, setAirplaneId] = useState("");
  const [minPrice, setMinPrice] = useState("");
  const [maxPrice, setMaxPrice] = useState("");

  const fetchFlights = async () => {
    try {
      const res = await axios.get(`${API_URL}/flights`, {
        headers: { Authorization: `Bearer ${token}` },
      });
      setFlights(res.data);
    } catch (err) {
      console.error("Failed to fetch flights", err);
    }
  };

  useEffect(() => {
    fetchFlights();
  }, []);

  const filteredFlights = flights.filter((flight) => {
    const matchesSearch =
      [flight.flightNumber, flight.source, flight.destination]
        .join(" ")
        .toLowerCase()
        .includes(searchTerm.toLowerCase());

    const matchesDate =
      !departureDate || flight.departureDate === departureDate;

    const matchesAirplane =
      !airplaneId || String(flight.airplaneId) === airplaneId;

    const price = parseFloat(flight.price);
    const min = parseFloat(minPrice) || 0;
    const max = parseFloat(maxPrice) || Infinity;
    const matchesPrice = price >= min && price <= max;

    return matchesSearch && matchesDate && matchesAirplane && matchesPrice;
  });

  return (
    <div className="admin-flights-container">
      <h2>All Flights</h2>

      <div className="filter-bar">
        <input
          type="text"
          placeholder="üîç Flight #, Source or Destination"
          value={searchTerm}
          onChange={(e) => setSearchTerm(e.target.value)}
        />
        <input
          type="date"
          value={departureDate}
          onChange={(e) => setDepartureDate(e.target.value)}
        />
        <input
          type="number"
          placeholder="Airplane ID"
          value={airplaneId}
          onChange={(e) => setAirplaneId(e.target.value)}
        />
        <input
          type="number"
          placeholder="Min Price"
          value={minPrice}
          onChange={(e) => setMinPrice(e.target.value)}
        />
        <input
          type="number"
          placeholder="Max Price"
          value={maxPrice}
          onChange={(e) => setMaxPrice(e.target.value)}
        />
      </div>

      {filteredFlights.length === 0 ? (
        <p>No flights found.</p>
      ) : (
        <ul className="flights-list">
          {filteredFlights.map((flight) => (
            <li key={flight.flightId}>
              ‚úàÔ∏è <strong>{flight.flightNumber}</strong><br />
              üìç {flight.source} ‚Üí {flight.destination}<br />
              üóì {flight.departureDate}<br />
              üïê {flight.departureTime} - {flight.arrivalTime}<br />
              üí∫ Airplane ID: {flight.airplaneId}<br />
              üí∞ ‚Çπ{flight.price}
            </li>
          ))}
        </ul>
      )}
    </div>
  );
};

export default Flights;
