import React, { useEffect, useState } from "react";
import { Link, useNavigate } from "react-router-dom";
import "animate.css";

const Navbar = () => {
  const [user, setUser] = useState(null);
  const navigate = useNavigate();

  // Check localStorage on mount
  useEffect(() => {
    const storedUser = localStorage.getItem("user");
    if (storedUser) {
      setUser(JSON.parse(storedUser));
    }
  }, []);

  const handleLogout = () => {
    localStorage.removeItem("token");
    localStorage.removeItem("user");
    setUser(null);
    navigate("/login");
  };

  return (
    <nav className="navbar animate__animated animate__fadeInDown animate__faster">
      <div className="navbar-brand">
        <Link to="/" className="logo">üõ´ FlyFast</Link>
      </div>

      <ul className="nav-links">
        {user ? (
          <>
            <li><Link to={user.userRole === "ADMIN" ? "/admin" : "/dashboard"}>üè† Dashboard</Link></li>
            <li><button onClick={handleLogout} className="logout-btn">üö™ Logout</button></li>
          </>
        ) : (
          <>
            <li><Link to="/login">üîë Login</Link></li>
            <li><Link to="/register">üìù Register</Link></li>
          </>
        )}
      </ul>
    </nav>
  );
};

export default Navbar;
