// src/App.js
import React, { useState } from "react";
import { BrowserRouter as Router, Routes, Route, Navigate } from "react-router-dom";
import LoginPage from "./pages/LoginPage";
import WalletPage from "./pages/WalletPage";
import FlightsPage from "./pages/FlightsPage";
import Navbar from "./components/Navbar";

function App() {
  const [token, setToken] = useState(localStorage.getItem("token") || "");
  const [userId, setUserId] = useState(localStorage.getItem("userId") || null);

  const handleLogin = (jwt, user) => {
    setToken(jwt);
    setUserId(user.userId);
    localStorage.setItem("token", jwt);
    localStorage.setItem("userId", user.userId);
  };

  const handleLogout = () => {
    setToken("");
    setUserId(null);
    localStorage.clear();
  };

  return (
    <Router>
      <Navbar token={token} onLogout={handleLogout} />
      <Routes>
        <Route path="/" element={token ? <Navigate to="/wallet" /> : <LoginPage onLogin={handleLogin} />} />
        <Route path="/wallet" element={<WalletPage token={token} userId={userId} />} />
        <Route path="/flights" element={<FlightsPage token={token} userId={userId} />} />
      </Routes>
    </Router>
  );
}

export default App;
