// src/App.js
import React, { useState } from "react";
import { BrowserRouter as Router, Routes, Route, Navigate } from "react-router-dom";
import Navbar from "./components/Navbar";
import LoginPage from "./pages/LoginPage";
import WalletPage from "./pages/WalletPage";
import BookingHistoryPage from "./pages/BookingHistoryPage";

function App() {
  const [token, setToken] = useState(localStorage.getItem("token") || "");
  const [userId, setUserId] = useState(localStorage.getItem("userId") || "");

  const handleLogin = (jwt, user) => {
    setToken(jwt);
    localStorage.setItem("token", jwt);
    setUserId(user.userId);
    localStorage.setItem("userId", user.userId);
  };

  const handleLogout = () => {
    setToken("");
    setUserId("");
    localStorage.removeItem("token");
    localStorage.removeItem("userId");
  };

  return (
    <Router>
      <Navbar token={token} handleLogout={handleLogout} />
      <Routes>
        <Route path="/" element={token ? <Navigate to="/wallet" /> : <LoginPage onLogin={handleLogin} />} />
        <Route path="/wallet" element={<WalletPage token={token} userId={userId} />} />
        <Route path="/bookings" element={<BookingHistoryPage token={token} userId={userId} />} />
        <Route path="*" element={<Navigate to="/" />} />
      </Routes>
    </Router>
  );
}

export default App;
